import React, {Component} from 'react';
import moment from 'moment';
import { connect } from 'react-redux';
import axios from 'axios';
import { Link } from 'react-router-dom';


class weather extends Component{

    componentDidMount(){
        console.log(this.props.city);
        axios.get("http://api.openweathermap.org/data/2.5/forecast?q="+this.props.city+"&appid=57808ae0e0093dd410d6cf81f988f653&units=imperial")
            .then(res=>{
                console.log(res.data.list);
                this.props.updateDays(res.data.list);
            })

        axios.get("http://api.openweathermap.org/data/2.5/weather?q="+this.props.city+"&appid=57808ae0e0093dd410d6cf81f988f653&units=imperial")
            .then(res=>{
                console.log(res);
                this.props.updateCurrent({temp: res.data.main.temp, icon:res.data.weather[0].icon, wind:res.data.wind.speed, feels_like:res.data.main.feels_like})
            })
    }

    handleChange=(e)=>{
        this.props.updateCity(e.target.value)
    }

    handleSubmit=(e)=>{
        e.preventDefault()
        axios.get("http://api.openweathermap.org/data/2.5/forecast?q="+this.props.city+"&appid=57808ae0e0093dd410d6cf81f988f653&units=imperial")
            .then(res=>{
                this.props.updateDays(res.data.list)

            }).catch(error=>{
                this.props.updateDays([])
                this.props.updateCurrent({temp: '-', icon: '-', wind: '-', feels_like:'-'})
            });

        axios.get("http://api.openweathermap.org/data/2.5/weather?q="+this.props.city+"&appid=57808ae0e0093dd410d6cf81f988f653&units=imperial")
            .then(res=>{
                console.log(res.data.main.temp)
                this.props.updateCurrent({temp: res.data.main.temp, 
                                          icon:res.data.weather[0].icon, 
                                          wind:res.data.wind.speed, 
                                          feels_like:res.data.main.feels_like})
            }).catch(error=>{
                this.props.updateDays([])
                this.props.updateCurrent({temp: '-', icon: '-', wind: '-', feels_like:'-'})
            })


    }

    render(){
        const dayList=[]
        var j=0
        var highestTemp=-1000
        var lowestTemp=1000

        for(var i=0;i<this.props.days.length;i++)
        {
            if(this.props.days[i].main.temp>highestTemp)
                highestTemp=this.props.days[i].main.temp
            if(this.props.days[i].main.temp<lowestTemp)
                lowestTemp=this.props.days[i].main.temp
            if(i===6 || i===14 || i===22 || i===30 || i===38)
            {
                dayList[j]={dt_txt:this.props.days[i].dt_txt,high:highestTemp,low:lowestTemp,icn:this.props.days[i].weather[0].icon}
                j++
                highestTemp=-1000
                lowestTemp=1000
            }
        }


        const forecast = dayList.length?(dayList.map(day=>{return (
            <div className="day" key={day.dt_txt}>
                <Link to={{ pathname: `/${moment(day.dt_txt).format('dddd')}`, state:{date:day.dt_txt}}}>
                    <p>{moment(day.dt_txt).format('ddd')}</p>
                    <p>{day.high}</p>
                    <p>{day.low}</p>
                    <p>{day.icn}</p>
                </Link>
            </div>
        )})):(<div style={{display:"none"}}>
            </div>)

        return(
            <div>
            <div>

                 <form onSubmit={this.handleSubmit}>
                    <div className="md-form active-pink-2 mb-3" >
                        <input className="form-control" type="text" placeholder="Search" aria-label="Search" onChange={this.handleChange} />
                    </div>
                 </form>
                 <div className="container">
                    <div className="city">{this.props.city}</div>
                    <div className="temp">{this.props.current.temp}</div>
                    <div className="wind">{this.props.current.wind}</div>
                    <div className="feels">{this.props.current.feels_like}</div>
                    <div className="icon"><img src={"http://openweathermap.org/img/wn/"+this.props.current.icon+"@2x.png"} /></div>
                 </div>
            </div>
            <div>
               {forecast}
            </div>
            </div>
        )
    }
}

const mapStateToProps = (state)=>{
    return{
        city:state.city,
        current:state.current,
        days:state.days
    }
}

const mapDispatchToProps = (dispatch)=>{
    return{
        updateCity: (city)=>{dispatch({type:'UPDATE_CITY',city:city})},
        updateCurrent: (current)=>{dispatch({type:'UPDATE_CURRENT',current:current})},
        updateDays: (days)=>{dispatch({type:'UPDATE_DAYS',days:days})}
    }
}


export default connect(mapStateToProps,mapDispatchToProps)(weather)
