import React, {Component} from 'react';
import moment from 'moment';
import { render } from '@testing-library/react';
import { connect } from 'react-redux';
import axios from 'axios';
import { Link } from 'react-router-dom';


class weather extends Component{

    constructor(props){
        super(props)
    }

    componentDidMount(){
        console.log(this.props.city);
        axios.get("http://api.openweathermap.org/data/2.5/forecast?q="+this.props.city+"&appid=57808ae0e0093dd410d6cf81f988f653&units=imperial")
            .then(res=>{
                console.log(res.data.list);
                this.props.updateDays(res.data.list);
            })

        axios.get("http://api.openweathermap.org/data/2.5/weather?q="+this.props.city+"&appid=57808ae0e0093dd410d6cf81f988f653&units=imperial")
            .then(res=>{
                console.log(res);
                this.props.updateCurrent({temp: res.data.main.temp, icon:res.data.weather[0].icon, wind:res.data.wind.speed, feels_like:res.data.main.feels_like})
            })
    }

    handleChange=(e)=>{
        this.props.updateCity(e.target.value)
    }

    handleSubmit=(e)=>{
        e.preventDefault()
        axios.get("http://api.openweathermap.org/data/2.5/forecast?q="+this.props.city+"&appid=57808ae0e0093dd410d6cf81f988f653&units=imperial")
            .then(res=>{
                this.props.updateDays(res.data.list)

            }).catch(error=>{
                this.props.updateDays([])
                this.props.updateCurrent({temp: '-', icon: '-', wind: '-', feels_like:'-'})
            });

        axios.get("http://api.openweathermap.org/data/2.5/weather?q="+this.props.city+"&appid=57808ae0e0093dd410d6cf81f988f653&units=imperial")
            .then(res=>{
                console.log(res.data.main.temp)
                this.props.updateCurrent({temp: res.data.main.temp, 
                                          icon:res.data.weather[0].icon, 
                                          wind:res.data.wind.speed, 
                                          feels_like:res.data.main.feels_like})
            }).catch(error=>{
                this.props.updateDays([])
                this.props.updateCurrent({temp: '-', icon: '-', wind: '-', feels_like:'-'})
            })


    }

    render(){
        console.log("hello")
        return(
            <div>
                 <form onSubmit={this.handleSubmit}>
                    <div>
                        <input type="text" onChange={this.handleChange}/>
                    </div>
                 </form>
            </div>
        )
    }
}

const mapStateToProps = (state)=>{
    return{
        city:state.city,
        current:state.current,
        days:state.days
    }
}

const mapDispatchToProps = (dispatch)=>{
    return{
        updateCity: (city)=>{dispatch({type:'UPDATE_CITY',city:city})},
        updateCurrent: (current)=>{dispatch({type:'UPDATE_CURRENT',current:current})},
        updateDays: (days)=>{dispatch({type:'UPDATE_DAYS',days:days})}
    }
}


export default connect(mapStateToProps,mapDispatchToProps)(weather)
