const initState={
    city:'Boston',
    current:{},
    days:[],
}

const rootreducer =(state=initState,action)=>{
    switch (action.type) {
        case 'UPDATE_CITY':
            return{
                ...state,
                city : action.city
            }
        case 'UPDATE_CURRENT':
            return{
                ...state,
                current:action.current
            }
        case 'UPDATE_DAYS':
            return{
                ...state,
                days:action.days
            }
        default:
            return state;
    }
}

export default rootreducer
