import React, { Component } from 'react';
import axios from 'axios';
import moment from 'moment';
import { connect } from 'react-redux';

class threehourly extends Component{

    componentDidMount(){

    }

    convertTime=(time)=>{
        console.log(time)
        const currTime=time.split(':')
        var tempTime
        var timeOfDay=""

        if (currTime[0]<12)
            timeOfDay="AM"
        else
            timeOfDay="PM"

        return currTime[0]+" "+timeOfDay
    }

    render(){
    var dateArray = this.props.location.state.date.split(' ')
    var dayList=[]
    var j=0
    for(var i=0;i<this.props.days.length;i++)
    {
        const currDateArray=this.props.days[i].dt_txt.split(' ')
        if(currDateArray[0]===dateArray[0])
        {
            dayList[j]=this.props.days[i]
            j++
        }
    }



    const allTimes = dayList.length?(
        dayList.map(day=>{
            return(
                <div>
                <p>{this.convertTime(day.dt_txt.split(' ')[1])}</p>
                <p>{day.weather[0].main}</p>
                <img src={"http://openweathermap.org/img/wn/"+day.weather[0].icon+"@2x.png"} />
                <p>{day.main.temp}</p>
                <p>{day.main.feels_like}</p>
                </div>
            )
    })):(<div style={{display:"none"}}>
    </div>)

        return(
            <div>
            {allTimes}
            </div>
        )
    }


}


const mapStateToProps = (state)=>{
    return{
        city:state.city,
        current:state.current,
        days:state.days
    }
}

export default connect(mapStateToProps)(threehourly)
